sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here
    try {
        var r = new sn_ws.RESTMessageV2('x_635228_iplookup.IPInfo', 'GetIPInfo');
        r.setStringParameterNoEscape('ipaddress', current.ip_address);
        r.setStringParameterNoEscape('var1', 'geo');

		gs.info("currentIPaDDRES = "+  current.ip_address);
        var response = r.execute();
        var responseBody = response.getBody();
        var httpStatus = response.getStatusCode();
		gs.info("responseBody = " +responseBody);

		//Converting the JSON formatted response body from response to javascript obj
        var responseObj = JSON.parse(responseBody);
        current.city = responseObj.city;
        gs.info("City = " + responseObj.city);
        current.update();

    } catch (err) {
        gs.info("Uncaught error: " + err);
        var message = err.message;
    }
})(current, previous);
